import { Component, Vue, Prop } from 'vue-property-decorator'
import Empty from '@/components/Empty/index.vue'
import {
  getOrderDetailPage,
  queryOrderDetailById,
  completeOrder,
  deliveryOrder,
  orderCancel,
  orderReject,
  orderAccept,
  getOrderListBy,
} from '@/api/order'
@Component({
  name: 'Orderview',
  components: {
    Empty,
  },
})
export default class extends Vue {
  @Prop({ default: '' }) orderStatics!: any

  private orderId = '' 
  private dialogOrderStatus = 0 //used in order alert
  private activeIndex = 0

  private dialogVisible = false //alert window
  private cancelDialogVisible = false 
  private cancelDialogTitle = '' 
  private cancelReason = ''
  private remark = '' //self defined reason
  private diaForm = []
  private row = {}
  private isAutoNext = true
  private isSearch: boolean = false
  private counts = 0
  private page: number = 1
  private pageSize: number = 10
  private status = 2
  private orderData = []
  private isTableOperateBtn = true
  private cancelOrderReasonList = [
    {
      value: 1,
      label: 'Sorry we are under excessive amount of orders',
    },
    {
      value: 2,
      label: 'Sorry, all items have been sold out',
    },
    {
      value: 3,
      label: 'Sorry, our restaurant is closed',
    },
    {
      value: 0,
      label: 'Other reasons',
    },
  ]

  private cancelrReasonList = [
    {
      value: 1,
      label: 'Sorry we are under excessive amount of orders',
    },
    {
      value: 2,
      label: 'Sorry, all items have been sold out',
    },
    {
      value: 3,
      label: 'Sorry, we are under shorthand of delivery person',
    },
    {
      value: 4,
      label: 'Customer called to cancel order',
    },
    {
      value: 0,
      label: 'Other reasons',
    },
  ]
  private orderList = [
    {
      label: 'All Orders',
      value: 0,
    },
    {
      label: 'Unpaid',
      value: 1,
    },
    {
      label: 'Pending Confirm',
      value: 2,
    },
    {
      label: 'Pending Deliver',
      value: 3,
    },
    {
      label: 'On delivery',
      value: 4,
    },
    {
      label: 'Completed',
      value: 5,
    },
    {
      label: 'Canceled',
      value: 6,
    },
  ]
  get tabList() {
    return [
      {
        label: 'Pending Confirm',
        value: 2,
        num: this.orderStatics.toBeConfirmed,
      },
      {
        label: 'Pending Deliver',
        value: 3,
        num: this.orderStatics.confirmed,
      },
    ]
  }
  created() {
    this.getOrderListData(this.status)
  }
  // get order list data
  async getOrderListData(status) {
    const params = {
      page: this.page,
      pageSize: this.pageSize,
      status: status,
    }
    const data = await getOrderDetailPage(params)
    this.orderData = data.data.data.records
    this.counts = data.data.data.total
    this.$emit('getOrderListBy3Status')
    if (
      this.dialogOrderStatus === 2 &&
      this.status === 2 &&
      this.isAutoNext &&
      !this.isTableOperateBtn &&
      data.data.records.length > 1
    ) {
      const row = data.data.records[0]
      this.goDetail(row.id, row.status, row, row)
    } else {
      return null
    }
  }

  //accept order
  orderAccept(row: any, event) {
    event.stopPropagation()
    this.orderId = row.id
    this.dialogOrderStatus = row.status
    orderAccept({ id: this.orderId })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success('Operated Successfully')
          this.orderId = ''
          // this.dialogOrderStatus = 0
          this.dialogVisible = false
          this.getOrderListData(this.status)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('Request Error: ' + err.message)
      })
  }
  //Open cancel order window
  cancelOrder(row: any, event) {
    event.stopPropagation()
    this.cancelDialogVisible = true
    this.orderId = row.id
    this.dialogOrderStatus = row.status
    this.cancelDialogTitle = 'Cancel'
    this.dialogVisible = false
    this.cancelReason = ''
  }
  //Open reject order window
  orderReject(row: any, event) {
    event.stopPropagation()
    this.cancelDialogVisible = true
    this.orderId = row.id
    this.dialogOrderStatus = row.status
    this.cancelDialogTitle = '拒绝'
    this.dialogVisible = false
    this.cancelReason = ''
  }
  //confirm cancel or reject orders and choose reason
  confirmCancel(type) {
    if (!this.cancelReason) {
      return this.$message.error(`Please choose the reason to ${this.cancelDialogTitle}`)
    } else if (this.cancelReason === 'Other reasons' && !this.remark) {
      return this.$message.error(`Please choose the reason to ${this.cancelDialogTitle}`)
    }

    ;(this.cancelDialogTitle === 'Cancel' ? orderCancel : orderReject)({
      id: this.orderId,
      // eslint-disable-next-line standard/computed-property-even-spacing
      [this.cancelDialogTitle === 'Cancel' ? 'cancelReason' : 'rejectionReason']:
        this.cancelReason === 'Other reasons' ? this.remark : this.cancelReason,
    })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success('Operated Successfully')
          this.cancelDialogVisible = false
          this.orderId = ''
          // this.dialogOrderStatus = 0
          this.getOrderListData(this.status)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('Request Error:' + err.message)
      })
  }

  // delivery and complete
  cancelOrDeliveryOrComplete(status: number, id: string, event) {
    event.stopPropagation()
    const params = {
      status,
      id,
    }
    ;(status === 3 ? deliveryOrder : completeOrder)(params)
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success('Operated Successfully')
          this.orderId = ''
          // this.dialogOrderStatus = 0
          this.dialogVisible = false
          this.getOrderListData(this.status)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('Request Error: ' + err.message)
      })
  }
  // check details
  async goDetail(id: any, status: number, row: any, event) {
    event.stopPropagation()
    // console.log(111, index, row)
    this.diaForm = []
    this.dialogVisible = true
    this.dialogOrderStatus = status
    const { data } = await queryOrderDetailById({ orderId: id })
    this.diaForm = data.data
    this.row = row
  }
  // close alert dialogue
  handleClose() {
    this.dialogVisible = false
  }
  // tab switch
  handleClass(index) {
    this.activeIndex = index
    if (index === 0) {
      this.status = 2
      this.getOrderListData(2)
    } else {
      this.status = 3
      this.getOrderListData(3)
    }
  }
  // trigger on a row fo table
  handleTable(row, column, event) {
    event.stopPropagation()
    this.goDetail(row.id, row.status, row, event)
  }
  // pagination
  private handleSizeChange(val: any) {
    this.pageSize = val
    this.getOrderListData(this.status)
  }

  private handleCurrentChange(val: any) {
    this.page = val
    this.getOrderListData(this.status)
  }
}
