import {getEmployeeList,enableOrDisableEmployee} from '@/api/employee'
import router from '@/router';

export default {
  data() {
    return {
      name: '',
      page: 1,
      pageSize: 10,
      total: 0,
      records: []
    }
  },
  created() {
    this.pageQuery()
  },
  methods: {
    pageQuery() {
      //page query
      const params = {name:this.name,page:this.page,pageSize:this.pageSize}
      getEmployeeList(params).then(res => {
        if(res.data.code === 1) {
          this.total = res.data.data.total
          this.records = res.data.data.records
        }
      }).catch(err => {
        this.$message.error('Request Error: ' + err.message)
      })
    },
    //change pageSize
    handleSizeChange(pageSize) {
      this.pageSize = pageSize
      this.pageQuery()
    },
    //change page
    handleCurrentChange(page) {
      this.page = page
      this.pageQuery()
    },
    //enable/disable 
    handleStartOrStop(row) {
      if(row.username === 'admin'){
        this.$message.error('Admin status cannot be changed!')
        return
      }

    //confirm alert
    this.$confirm('Are you confirmed to change the status?', 'Info', {
          confirmButtonText: 'Confirm',
          cancelButtonText: 'Cancel',
          type: 'warning'
        }).then(() => {
          const p = {
            id: row.id,
            status: !row.status ? 1 : 0
          }
          enableOrDisableEmployee(p).then(res => {
            if(res.data.code === 1) {
              this.$message.success('Employee status has been successfully changed!')
              this.pageQuery()
            }
          })
        })
    },
    //change to add page
    handleAddEmp(){
      this.$router.push('/employee/add')
    },
    //change to edit page
    handleUpdateEmp(row) {
      if(row.username === 'admin'){
        this.$message.error('Admin profile cannot be changed!')
        return 
      }
      this.$router.push({
        path: '/employee/add',
        query: {id: row.id}
      })
    }
  }
}
